import { createMachine, assign, StateFrom } from 'xstate';

const TicketTypes = ["ADULT", "CHILD"]
type TicketType = typeof TicketTypes[number]

export type BookingMachineState = StateFrom<typeof bookingMachine>;

export const bookingMachine = 
/** @xstate-layout -kWuEI5E2+p2y4OhCKyiLfRWCy2CmGCJ2H0Euz+oNB3VOYkBcN882wqiIuAAVwANnQzonJTjHbo0yEouYAYszFTev4wi5DCDHoMgVXIzXBW3GkmpW1dDKTOZLK4mc5XOq5vjHnNngPggFdE52bEgA */
createMachine({
  id: "bookingMachine",
  tsTypes: {} as import("./booking.machine.typegen").Typegen0,
  schema: {
    context: {} as {
      seats: boolean[];
      tickets: {[key: TicketType]: number};
    },
    events: {} as
      | { type: "UPDATE_SEAT_SELECTION"; selections?: string[] }
      | { type: "CONFIRM_SEAT_SELECTION"; }
      | { type: "USE_VOUCHERS"; }
      | { type: "PAY"; }
  },
  initial: "seats",
  context: {
    seats: [false, false, false, false, false],
    tickets: {},
  },
  states: {
    seats: {
      on: {
        UPDATE_SEAT_SELECTION: {
          actions: "storeSeatSelection"
        },
        CONFIRM_SEAT_SELECTION: {
          target: "tickets",
        },
      },
    },
    tickets: {
      on: {
        USE_VOUCHERS: {
          target: "perks",
        },
        PAY: {
          target: "result",
        },
      },
    },
    perks: {
      on: {
        PAY: {
          target: "result",
        },
      },
    },
    result: {},
  },
},
{
  actions: {
    storeSeatSelection: assign({
      seats: (ctx, event) => {
        // create a new array
        // iterate over ctx.seats
        // if event.selections contains the index, set the value to true in the new array
        // else set the value to false
        // return the new array
        console.log("trying to store seat selection", event);
        const selections = event.selections || [];
        return ctx.seats.map((_, index) => selections.includes(index.toString()));
      },
    })
  }
});